mapper.select:
SELECT	COUNT(ACCOUNT)
FROM	MEMBER
WHERE	EMAIL = #{email}

SELECT	COUNT(ACCOUNT)
FROM	MEMBER
WHERE	NICKNAME = #{nickname}

mapper.insert:
INSERT	INTO MEMBER (
	ACCOUNT
	, EMAIL
	, NICKNAME
	, PASSWORD
	, REGIST_DATE
	, SALT
)
VALUES (
	MEMBER_ACCOUNT_SEQ.NEXTVAL
	, #{email}
	, #{nickname}
	, #{password}
	, SYSDATE
	, #{salt}
)

mapper.select:
SELECT	NVL(SALT,'')
FROM	MEMBER
WHERE	EMAIL = ${email}
